import React, { useState } from "react";
import "./StopWatch.css";
import Menu from "../Menu/Menu";
import ControlButtons from "../ContolButtons/ControlButtons";
import Timers from "../Timers/Timers";

function StopWatch() {
const [isActive, setIsActive] = useState(false);
const [isPaused, setIsPaused] = useState(true);
const [time, setTime] = useState(0);
const [goBack, setGoBack] = useState(false);
const [isStopwatch, setIsStopwatch] = useState(true)
const [timersArray, setTimersArray] = useState([0])

React.useEffect(() => {
	let interval = null;

	if (isActive && isPaused === false) {
	interval = setInterval(() => {
		setTime((time) => time + 10);
	}, 10);
	} else {
	clearInterval(interval);
	}
	return () => {
	clearInterval(interval);
	};
}, [isActive, isPaused]);

const handleStart = () => {
	setIsActive(true);
	setIsPaused(false);
};

const handlePauseResume = () => {
	setIsPaused(!isPaused);
};

const handleReset = () => {
	setIsPaused(true);
	setIsActive(false);
	setTime(0);
};

const handleStopwatch = () => {
	setIsStopwatch(true);
	setTimersArray([0])
	setTime(0);
	setGoBack(true);
};

const handleIntevals = () => {
	setIsStopwatch(false);
	setTimersArray([3000,3000,3000])
	setTime(0);
	setGoBack(true);
}

const handleGoBack = () => {
	setGoBack(false);
	handleReset();
};

const handleAddTimer = () =>{
	setTimersArray([0,0,0])
};

const handleRemoveTimer = () =>{

};

return (
	<div className="stop-watch">
	<Menu handleStopwatch={handleStopwatch} handleIntevals={handleIntevals} handleGoBack={handleGoBack} goBack={goBack} />
	<Timers 
		time={time} 
		timersArray={timersArray} 
		handleAddTimer={handleAddTimer} 
		handleRemoveTimer={handleRemoveTimer}
		isStopwatch={isStopwatch}
	/>
	<ControlButtons
		active={isActive}
		isPaused={isPaused}
		handleStart={handleStart}
		handlePauseResume={handlePauseResume}
		handleReset={handleReset}
	/>
	</div>
);
}

export default StopWatch;
