import React from "react";
import "./Timer.css";

export default function Timer(props) {
	let timer = props.countdown;
	if (props.index === 0) {
		timer = props.countdown - props.time;
		if(props.time > props.countdown){
			timer = 0;
	} else {
		if(props.time > props.timersArray[props.index-1]){
			timer = props.countdown - props.time + props.timersArray[props.index-1] ;
		}
	}
	}
	return (
		<div id={"timer-" + props.index} className="timer">
			<span className="digits">
				{("0" + Math.floor((timer / 60000) % 60)).slice(-2)}:
			</span>
			<span className="digits">
				{("0" + Math.floor((timer / 1000) % 60)).slice(-2)}.
			</span>
			<span className="digits mili-sec">
				{("0" + ((timer / 10) % 100)).slice(-2)}
			</span>
		</div>
	);
}
